  from decimal import Decimalfrom pprint import pprintimport boto3from botocore.exceptions import ClientErrordef delete_underrated_movie(title, year, rating, dynamodb=None):dynamodb = boto3.resource('dynamodb')table = dynamodb.Table('Movies')try:response = table.delete_item(Key={'year': year,'title': title})except ClientError as e:if e.response['Error']['Code'] == &quot;ConditionalCheckFailedException&quot;:print(e.response['Error']['Message'])else:raiseelse:return responseif __name__ == '__main__':print(&quot;Attempting a conditional delete...&quot;)delete_response = delete_underrated_movie(&quot;The Big New Movie&quot;, 2015, 5)if delete_response:print(&quot;Delete movie succeeded:&quot;)pprint(delete_response, sort_dicts=False) Chạy chương trình bằng Window Command Prompt đã được xác thực và cấu hình ở bước 3.1 Cấu hình AWS CLI. Gõ lệnh:  python MoviesItemOps03.pyTrường hợp khác: sử dụng python &quot;đường dẫn file MoviesItemOps03.py&quot;
